import React from "react";
import { Link } from "react-router-dom";

export function Home() {
  return (
    <div className="relative min-h-screen flex flex-col lg:flex-row items-center justify-center bg-gradient-to-r from-black via-[var(--primary-color)] to-[var(--secondary-color)] text-white px-10 lg:px-20">
      {/* Texto Principal */}
      <div className="text-center lg:text-left max-w-2xl">
        <h1 className="text-5xl font-extrabold tracking-wide leading-tight">
          Olá, sou Rogério Fontes!
        </h1>
        <p className="text-lg text-gray-300 mt-4">
          Empreendedor, mentor de startups, apresentador e apaixonado por inovação e tecnologia.
        </p>
        <div className="mt-6">
          <Link to="/about">
            <button className="bg-secondary text-white px-6 py-3 rounded-lg shadow-md hover:bg-blue-500 hover:scale-105 transition-all duration-300">
              Me Conheça Melhor
            </button>
          </Link>
        </div>
      </div>

      {/* Imagem de Destaque */}
      <div className="mt-8 lg:mt-0 lg:ml-12">
        <img src="/images/profile.png" alt="Rogério Fontes" className="w-80 h-80 object-cover rounded-full shadow-lg border-4 border-secondary" />
      </div>
    </div>
  );
}
