import React from "react";
import { Link } from "react-router-dom";

export function Home() {
  return (
    <div className="p-12 text-center flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-black via-[var(--primary-color)] to-[var(--secondary-color)] pt-24">
      <h1 className="text-5xl font-extrabold text-white">Rogério Fontes de Resende</h1>
      <p className="text-xl text-gray-700 mt-4">Empreendedor | Mentor de Startups | CEO | Apresentador</p>
      <p className="mt-6 text-lg text-gray-300 max-w-2xl">
        Apaixonado por tecnologia, inovação e negócios. Ativista e entusiasta da causa vegana.
        Trabalho com startups, mentoria, aceleração de negócios e criação de soluções inovadoras.
      </p>
      <img src="/images/profile.png" alt="Rogério Fontes" className="w-40 h-40 rounded-full mt-6 shadow-lg"/>
      <Link to="/works">
        <button className="mt-8 px-8 py-3 bg-[var(--primary-color)] text-white rounded-lg shadow-md hover:bg-[var(--secondary-color)] hover:scale-110 hover:shadow-blue-400 transition-transform duration-300">
          Conheça Meus Projetos
        </button>
      </Link>
    </div>
  );
}
