import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { Home } from "./pages/Home";
import { About } from "./pages/About";
import { Works } from "./pages/Works";
import { Contact } from "./pages/Contact";
import { AiOutlineHome, AiOutlineUser, AiOutlineProject, AiOutlineMail } from "react-icons/ai";

function App() {
  return (
    <div className="bg-primary text-white min-h-screen">
      <Router>
        <nav className="bg-black p-6 text-white flex justify-between items-center shadow-md fixed top-0 left-0 w-full z-10">
          <div className="text-xl font-bold tracking-wide">Rogerio Fontes</div>
          <div className="flex space-x-8 text-lg">
            <Link to="/" className="hover:text-blue-400 transition duration-300 flex items-center text-white">
              <AiOutlineHome className="mr-2" /> Home
            </Link>
            <Link to="/about" className="hover:text-blue-400 transition duration-300 flex items-center text-white">
              <AiOutlineUser className="mr-2" /> Sobre
            </Link>
            <Link to="/works" className="hover:text-blue-400 transition duration-300 flex items-center text-white">
              <AiOutlineProject className="mr-2" /> Trabalhos
            </Link>
            <Link to="/contact" className="hover:text-blue-400 transition duration-300 flex items-center text-white">
              <AiOutlineMail className="mr-2" /> Contato
            </Link>
          </div>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/works" element={<Works />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
        <footer className="bg-black text-white text-center p-4 mt-8">
          <p>&copy; 2025 Rogerio Fontes. Todos os direitos reservados.</p>
        </footer>
      </Router>
    </div>
  );
}

export default App;
