import React from "react";
import { Link } from "react-router-dom";

export function Home() {
  return (
    <div className="relative min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-black via-[var(--primary-color)] to-[var(--secondary-color)] text-white text-center p-12">
      <h1 className="text-5xl font-extrabold tracking-wide">Rogério Fontes de Resende</h1>
      <p className="text-xl text-gray-300 mt-4">Empreendedor | Mentor de Startups | CEO | Apresentador</p>
      <p className="mt-6 text-lg text-gray-300 max-w-2xl">
        Apaixonado por tecnologia, inovação e negócios. Ativista e entusiasta da causa vegana.
        Trabalho com startups, mentoria, aceleração de negócios e criação de soluções inovadoras.
      </p>
      <img src="/images/profile.png" alt="Rogério Fontes" className="w-40 h-40 rounded-full mt-6 shadow-lg"/>
      <div className="mt-6">
        <Link to="/works">
          <button className="bg-secondary text-white px-6 py-3 rounded-lg shadow-md hover:bg-blue-500 hover:scale-105 transition-all duration-300">
            Conheça Meus Projetos
          </button>
        </Link>
      </div>
    </div>
  );
}
